sadness = 0
kpop = 0
weeb = 0
popularity = 0
for thing in arsalaan:
    print(thing["name"])
    for genre in thing["genres"]:
        genreWords = genre.split()
        if genre == "slowed and reverb" or any(x in genreWords for x in ['emo', 'sad']):
            sadness+=1
        if any(x in genreWords for x in ['k-pop', 'korean']):
            kpop+=1
        if any(x in genreWords for x in ['j-pop', 'japanese', "anime"]):
            weeb+=1
    popularity += thing["popularity"]

print(popularity/20)
print(sadness, kpop, weeb)
